function initMap(){new google.maps.Map(document.getElementById("googleMap"),{zoom:15,center:{lat:55.85997,lng:-4.25272},panControl:!1,mapTypeControl:!1,zoomControlOptions:{style:google.maps.ZoomControlStyle.SMALL,position:google.maps.ControlPosition.TOP_RIGHT}})}function loadScript(){var o=document.createElement("script");o.src="https://maps.googleapis.com/maps/api/js?key=AIzaSyBdc1lhKaZ7X9xToo62bd1PZIEo6TTrdjA&callback=initMap",document.body.appendChild(o)}window.onload=loadScript;